$count = 0
foreach ($replacement in $replacements) {
    if ($content -match [regex]::Escape($replacement['pattern'])) {
        $content = $content -replace [regex]::Escape($replacement['pattern']), $replacement['svg']
        $count++
        Write-Host "✓ $count: Substituído"
    }
}

[System.IO.File]::WriteAllText($file, $content, [System.Text.Encoding]::UTF8)

Write-Host "`n✅ Concluído!"
Write-Host "Total de ícones substituídos: $count"
