# Função para substituir o ícone
function ReplaceIcon($text, $svg) {
    $pattern = "<span class=""material-symbols-outlined"">$text</span>"
    $replacement = $svg
    
    if ($content -match [regex]::Escape($pattern)) {
        return $pattern, $replacement
    }
    return $null
}

# Executar substituições
$count = 0
foreach ($iconName in $iconMap.Keys) {
    # Encontrar e substituir todas as instâncias
    $pattern = "<span class=""material-symbols-outlined"">$iconName</span>"
    $svg = $iconMap[$iconName]
    
    if ($content -match [regex]::Escape($pattern)) {
        $content = $content -replace [regex]::Escape($pattern), $svg
        $count++
        Write-Host "✓ Substituído: $iconName ($count)"
    }
}

# Salvar arquivo
Set-Content $htmlFile $content -Encoding UTF8 -NoNewline

Write-Host "`n================================"
Write-Host "✓ Script concluído!"
Write-Host "================================"
Write-Host "Total de ícones substituídos: $count"
Write-Host "Arquivo: $htmlFile"
Write-Host "`nOs ícones Material Symbols foram substituídos por Lucide SVG."
Write-Host "Próximo passo: Fazer deploy das mudanças"
Write-Host "Próximo passo: Fazer deploy das mudanças"
